public class QuebraVigenere {
	private static final String CRIPTOGRAMA = "";

	public static void main(String[] args) {
		for (int tamanhoSenha = 3 ; tamanhoSenha < 15 ; tamanhoSenha++) {
			System.out.println("===================================");
			System.out.println("=== Teste de Senha de Tamanho " + tamanhoSenha + "===");
			for (int numeroConjunto = 0 ; numeroConjunto < tamanhoSenha ; numeroConjunto++) {
				int[] vetorOcorrencia = new int[256];
				for (int letra = 0 ; letra < CRIPTOGRAMA.length(); letra+=2) {
					if (((letra / 2) % tamanhoSenha) == numeroConjunto) {
						vetorOcorrencia[Integer.parseInt(CRIPTOGRAMA.substring(letra, letra + 2), 16)]++;
					}
				}
				int maior = 0;
				int letra = 0;
				for (int i = 0 ; i < 256 ; i++) {
					if (vetorOcorrencia[i] > maior) {
						letra = i;
						maior = vetorOcorrencia[i];
					}
				}
				System.out.println("Conjunto " + numeroConjunto + ": " + ((char) (letra ^ 32)));
			}
		}
	}		
}
